package soloworld.scalar.string;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

public class LongestPalindromeTest {

    private LongestPalindrome longestPalindrome;


    @BeforeEach
    void setUp() {
        longestPalindrome = new LongestPalindrome();
    }

    @Test
    void test1() {
       assertEquals("aaabaaa", longestPalindrome.longestPalindrome("aaaabaaa"));


    }@Test
    void test12() {
       assertEquals("bb", longestPalindrome.longestPalindrome("bb"));


    }@Test
    void test123() {
       assertEquals("a", longestPalindrome.longestPalindrome("a"));


    }
    @Test
    void test1234() {
       assertEquals("", longestPalindrome.longestPalindrome(""));


    }
}